import React, { useRef, useEffect, useState } from 'react';

const EmojiImage = ({ emojis }) => {
	const [backgroundColor, setBackgroundColor] = useState('#fff');

	const validateHex = (hex) => {
		const hexRegex = /^#([A-Fa-f0-9]{6})$/;
		console.log(hexRegex.test(hex));
	};

	const canvasRef = useRef(null);
	const factor = 0.65;
	const scale = { width: (window.innerWidth / emojis[0].length) * factor, height: (window.innerHeight / emojis.length) * factor };
	console.log(scale);

	const draw = (ctx) => {
		ctx.fillStyle = backgroundColor;
		ctx.fillRect(0, 0, ctx.canvas.width, ctx.canvas.height);
		ctx.fillStyle = '#000000';
		const fontSize = ctx.canvas.width / (emojis[0].length * (10 / 8));
		console.log(fontSize);
		ctx.font = fontSize + 'px serif';
		for (let i = 0; i < emojis.length; i++) {
			ctx.fillText(emojis[i], 0, i * scale.height);
		}
		ctx.fill();
	};

	useEffect(() => {
		const canvas = canvasRef.current;
		const context = canvas.getContext('2d');
		let animationFrameId;

		draw(context);

		return () => {
			window.cancelAnimationFrame(animationFrameId);
		};
	});

	return (
		<div>
			<input type="text" onChange={(e) => validateHex(e.target.value)} />
			<canvas ref={canvasRef} width={emojis[0].length * scale.width + 'px'} height={emojis.length * scale.height + 'px'} style={{ marginLeft: '10%' }}></canvas>
		</div>
	);
};

export default EmojiImage;
