import React, { useRef, useEffect } from 'react';

const EmojiImage = ({ emojis }) => {
	const canvasRef = useRef(null);

	const scale = { width: (window.innerWidth / emojis[0].length) * 0.95, height: (window.innerHeight / emojis.length) * 0.95 };
	console.log(scale);

	const draw = (ctx) => {
		ctx.fillStyle = '#000';
		ctx.fillRect(0, 0, ctx.canvas.width, ctx.canvas.height);
		ctx.fillStyle = '#000000';
		ctx.font = '10px serif';
		ctx.fillText('ðŸ”¥', 50, 90);
		ctx.fill();
	};

	useEffect(() => {
		const canvas = canvasRef.current;
		const context = canvas.getContext('2d');
		let animationFrameId;

		draw(context);

		return () => {
			window.cancelAnimationFrame(animationFrameId);
		};
	}, []);

	return (
		<div>
			{/* {emojis.map((row, rowindex) => {
				return (
					<div key={'row:' + rowindex} style={{ padding: 0, height: '7px', width: '95%', margin: '0 auto' }}>
						{row.map((emoji, index) => {
							return (
								<p
									key={rowindex + ':' + index}
									style={{ display: 'inline-block', margin: 0, padding: '1px', fontSize: 100 / row.length + 'em', width: 100 / row.length + '%', boxSizing: 'border-box' }}
								>
									{emoji === 'empty' ? '\u00A0' : emoji}
								</p>
							);
						})}
					</div>
				);
			})} */}
			<canvas ref={canvasRef} width={emojis[0].length * scale.width + 'px'} height={emojis.length + 'px'}></canvas>
		</div>
	);
};

export default EmojiImage;
