import React, { useRef, useEffect } from 'react';

const EmojiImage = ({ emojis, backgroundColor, scaling, dimensions }) => {
	const canvasRef = useRef(null);
	const scale = { width: (dimensions.width / emojis[0].length) * scaling, height: (dimensions.height / emojis.length) * scaling };

	const draw = (ctx) => {
		ctx.fillStyle = backgroundColor;
		ctx.fillRect(0, 0, ctx.canvas.width, ctx.canvas.height);
		ctx.fillStyle = '#000000';
		const fontSize = ctx.canvas.width / (emojis[0].length * (10 / 8));
		ctx.font = fontSize + 'px serif';
		for (let i = 0; i < emojis.length; i++) {
			ctx.fillText(emojis[i], 0, i * scale.height);
		}
		ctx.fill();
	};

	useEffect(() => {
		const canvas = canvasRef.current;
		const context = canvas.getContext('2d');
		let animationFrameId;

		draw(context);

		return () => {
			window.cancelAnimationFrame(animationFrameId);
		};
	});

	return (
		<div>
			<canvas ref={canvasRef} width={emojis[0].length * scale.width + 'px'} height={emojis.length * scale.height + 'px'}></canvas>
		</div>
	);
};

export default EmojiImage;
